mapper.select:
        SELECT
        

--         级联表的字段
        ,fanzhashipin.fanzhashipin_name as fanzhashipinName
        ,fanzhashipin.fanzhashipin_types as fanzhashipinTypes
        ,fanzhashipin.fanzhashipin_photo as fanzhashipinPhoto
        ,fanzhashipin.fanzhashipin_video as fanzhashipinVideo
        ,fanzhashipin.zan_number as zanNumber
        ,fanzhashipin.cai_number as caiNumber
        ,fanzhashipin.fanzhashipin_content as fanzhashipinContent
        ,fanzhashipin.fanzhashipin_delete as fanzhashipinDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_delete as yonghuDelete

        FROM fanzhashipin_collection  a
        left JOIN fanzhashipin fanzhashipin ON a.fanzhashipin_id = fanzhashipin.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.fanzhashipin_id = #{params.fanzhashipinId}
            
            
                and a.yonghu_id = #{params.yonghuId}
            
            
                and a.fanzhashipin_collection_types = #{params.fanzhashipinCollectionTypes}
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

                
            
                and a.fanzhashipin_id IS NOT NULL
            
            
                and fanzhashipin.fanzhashipin_name like CONCAT('%',#{params.fanzhashipinName},'%')
            
            
                and fanzhashipin.fanzhashipin_types = #{params.fanzhashipinTypes}
            

            
                and fanzhashipin.fanzhashipin_video like CONCAT('%',#{params.fanzhashipinVideo},'%')
            
            
                  and fanzhashipin.zan_number >= #{params.zanNumberStart}   
            
            
                  and fanzhashipin.zan_number <= #{params.zanNumberEnd}   
            
            
                and fanzhashipin.zan_number = #{params.zanNumber}
            
            
                  and fanzhashipin.cai_number >= #{params.caiNumberStart}   
            
            
                  and fanzhashipin.cai_number <= #{params.caiNumberEnd}   
            
            
                and fanzhashipin.cai_number = #{params.caiNumber}
            
            
                and fanzhashipin.fanzhashipin_content like CONCAT('%',#{params.fanzhashipinContent},'%')
            
            
                  and fanzhashipin.fanzhashipin_delete >= #{params.fanzhashipinDeleteStart}   
            
            
                  and fanzhashipin.fanzhashipin_delete <= #{params.fanzhashipinDeleteEnd}   
            
            
                and fanzhashipin.fanzhashipin_delete = #{params.fanzhashipinDelete}
            
                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   
            
            
                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   
            
            
                and yonghu.yonghu_delete = #{params.yonghuDelete}
            
        

        order by a.${params.orderBy} desc
