mapper.select:
        SELECT
        

--         级联表的字段

        FROM fanzhashipin  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.fanzhashipin_name like CONCAT('%',#{params.fanzhashipinName},'%')
            
            
                and a.fanzhashipin_types = #{params.fanzhashipinTypes}
            
            
                and a.fanzhashipin_video like CONCAT('%',#{params.fanzhashipinVideo},'%')
            
            
                  and a.zan_number >= #{params.zanNumberStart}   
            
            
                  and a.zan_number <= #{params.zanNumberEnd}   
            
             
                and a.zan_number = #{params.zanNumber}
             
            
                  and a.cai_number >= #{params.caiNumberStart}   
            
            
                  and a.cai_number <= #{params.caiNumberEnd}   
            
             
                and a.cai_number = #{params.caiNumber}
             
            
                and a.fanzhashipin_content like CONCAT('%',#{params.fanzhashipinContent},'%')
            
            
                  and a.fanzhashipin_delete >= #{params.fanzhashipinDeleteStart}   
            
            
                  and a.fanzhashipin_delete <= #{params.fanzhashipinDeleteEnd}   
            
             
                and a.fanzhashipin_delete = #{params.fanzhashipinDelete}
             

        

        order by a.${params.orderBy} desc
